mapper.insert:
		
			select
			NVL(max(rp_id),0) +1 from report
			

		
		insert into report(rp_id
						,user_id
						,rp_user_id
						,rp_type
		        		,rp_contents
		        		,board_num
		)
		
		values( 	 #{rpId}
					,#{userId}
					,#{rpUserId}
					,#{rpType}
					,#{rpContents}
					,#{boardNum}
		)

mapper.select:
SELECT b.* FROM ( SELECT rownum no, a.* FROM ( select re_id as "reId", user_id as "userId", contents as "contents", to_char(write_date, 'YY/MM/DD') as "writeDate" board_num as "boardNum" from re
where search_id = #{searchId}
order by #{orderby} desc
order by re_id desc
) a ) b WHERE no BETWEEN #{first} AND #{last}
