mapper.insert:
		
			select NVL(max(search_id),0) +1 from search

			
		
		insert into search(search_id
							,user_id
							,title
							,contents
		)

		values(#{searchId}
				,#{userId}
				,#{title}
				,#{contents}
		)

mapper.select:
SELECT b.* FROM ( SELECT rownum no, a.* FROM ( select search_id as "searchId", user_id as "userId", title as "title", contents as "contents", to_char(write_date, 'YY/MM/DD') as "writeDate" from search
and title like '%' || #{title} || '%'
order by #{orderby} desc
order by search_id desc
) a ) b WHERE no BETWEEN #{first} AND #{last}

mapper.update:
update search 

	title = #{title},
	contents = #{contents} 

where search_id = #{searchId}
