mapper.select:
SELECT tablespace_name "tablespaceName"
FROM user_tbspc_tb
WHERE user_id = #{userId}

SELECT d.tablespace_name "tablespaceName" , d.status "status" , sum(df.bytes/1024/1024) "total" , sum(fs.bytes/1024/1024) "free" , sum(df.bytes/1024/1024) - sum(fs.bytes/1024/1024) "used" FROM dba_tablespaces d, user_tbspc_tb u, dba_data_files df, dba_free_space fs WHERE u.user_id = #{userId} AND d.tablespace_name = u.tablespace_name
AND u.tablespace_name = #{tablespaceName}
AND d.tablespace_name = df.tablespace_name AND df.file_id = fs.file_id(+) GROUP BY d.tablespace_name, d.status ORDER BY 1
